mapper.select:
select *
from orders

    user_id=#{currentId}


        and status=#{status}



order by order_time desc

select *
from orders


        and phone like concat('%',#{phone},'%')


        and status=#{status}


        and number like concat('%',#{number},'%')


        and order_time >= #{beginTime}


        and order_time <= #{endTime}}



order by order_time desc

select sum(amount) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select od.name, sum(od.number) number from order_detail od ,orders oe where od.order_id=oe.id and oe.status=5
and oe.order_time > #{begin}
and oe.order_time < #{end}
group by od.name order by number desc limit 0,10
